import { useState } from 'react';
import "./styles.css";


export default function TaskList({tasks, onChangeTask, onDeleteTask}) {
  return (
    <ul>
      {tasks.map((task) => (
        <li key={task.id}>
          <Task task={task} onChange={onChangeTask} onDelete={onDeleteTask} />
        </li>
      ))}
    </ul>
  );
}

function Task({task, onChange, onDelete}) {
  const [isEditing, setIsEditing] = useState(false);
  let taskContent;
  if (isEditing) {
    taskContent = (
      <>
      <table>
        <th>
          <td>Описание</td>
          <td>Кабинет</td>
        </th>
        <tr>
          <td>
            <input
              value={task.text}
              onChange={(e) => {
                onChange({
                  ...task,
                  text: e.target.value,
                });
              }}
            />
          </td>
          <td>
            <input
              value={task.cabinet}
              onChange={(e) => {
                onChange({
                  ...task,
                  cabinet: e.target.value,
                });
              }}
            />
            </td>
          <td><button class="SaveButton" onClick={() => setIsEditing(false)}>Сохранить</button></td>
        </tr>
        </table>
      </>
    );
  } else {
    taskContent = (
      <>
        <tr>
          <td><div>{task.text}</div></td>
          <td><div>{task.cabinet}</div></td>
          <td><button class="EditButton" onClick={() => setIsEditing(true)}>Редактировать</button></td>
          <td><button class="DeleteButton" onClick={() => onDelete(task.id)}>Удалить</button></td>
        </tr>
      </>
    );
  }
  return (
    <label>
      {taskContent}
    </label>
  );
}
