import { useState } from 'react';

export default function AddTask({onAddTask}) {
  const [text, setText] = useState('');
  return (
    <>
      <input
        class="DescriptionTask"
        placeholder="Add task"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <input
        class="NumberCabinet"
        placeholder="Кабинет"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <button
        onClick={() => {
          setText('');
          onAddTask(text);
        }}>
        Добавить
      </button>
    </>
  );
}
